INSERT INTO `users` VALUES (1,'test','test','test@test.com','$2y$10$5oIn0SoMR8.gFfB7D7weRO5zyFeNVFLFwrB6dxGeV/nh2q3VR9MLi','This is just a test.\r\nA motherfucking test','2022-02-10 21:31:16',NULL),(2,'ACEM','TECH','geeks@acem.tech','$2y$10$2uileiL6dmcHnsiZLdiGbeafjbrijrdS1euSxXkksMgsA/wYqQ5Ja','LACEM TECH est une solution innovante dans le simple but de garantir lenrichissement et la valorisation des projets innovants, de cultiver lesprit dinnovation, développer et faciliter lesprit dépanouissement.','2022-02-12 19:44:00','d2bcfa44319033e0889a4f75da7fcb246647abab.png'),(3,'MOHAMED','M&#39;madi Hassane','mmadihassane342@gmail.com','$2y$10$d8.LtfWN1n.Ovcw4sXxhwerhmKNcaAtC1BQgQArbc9aeQoi4GDPge','Doctorant en énergétique et énergies renouvelables/    \r\n                 \r\nIngénieur d&#39;Etat en Conception Mécanique et Innovation','2022-02-13 20:23:06',NULL),(4,'ABDEREMANE','IBRAHIM AHMED','ibrahimabderemane07@gmail.com','$2y$10$t3h3E.5EbWSu1KbUpR8e5OOBIf96qwtWhXq.pPS5R5aMunMDvnEYa','Etudiant en Master 2 Systèmes Intelligents et Energie','2022-02-13 21:36:31',NULL),(5,'Ankidine','Ibrahim','ankicomorien@gmail.com','$2y$10$qOX.Z3epTs5E2mlfPoEesOJz041OPUKm2rpSzLcKexocqS8YiFlEC','Je fais Sciensces Mathématiques et Informatique (SMI), Jeffectue cette formation à la Faculté des Sciences Dhar El Mahraz Fès (FSDM).','2022-02-28 18:20:36','ae0ed35a66e468052ee197b25e8a8959159d34ae.jpeg'),(6,'Sidi Ahmed','Djawad','djaoidahmedsidi@gmail.com','$2y$10$luUtp6qPxwKfyq4dt8ZZ9uLQS.puLEHQuh6Pr0J7zwQm93eXiai7C','VP ACEM 2022\r\nIngénierie Conception et Développement des Application','2022-06-06 15:47:58',NULL),(7,'ACEM','BE','acemaroc@gmail.com','$2y$10$0kKcTL2OrAl.BU92gDgV4evQ0fXE0QJTb6g76Iw9GUKaO6/EzqOsu','Bureau Exécutif de lACEM','2022-06-06 23:23:06','654992a56176f6cc101227a55fd304704cc8a971.png'),(8,'Djaloud','Mohamed','mrdjaloud@gmail.com','$2y$10$Uo0qneqvFrNJrpsjHZU8NODa72PjL4EZArB6JYyHoiq.GUmSXtn42','Djaloud med','2022-06-07 00:11:50',NULL),(9,'Mohamed','Issihaka','issihaka96@gmail.com','$2y$10$csXcai.IM2gkE0dWS8dpJOB3oLQj2W.9vfJ2jymQr5drMvb33/rui','Coordinateur du Comité Scientifique de l&#39;ACEM','2022-06-13 22:44:26',NULL),(10,'Roushnaty','Ali yamani','princessenatyamani@gmail.com','$2y$10$9YZ.No2H8j4saS5zLugOD.EJSs95coyHlZEere8ZZkyPSPMoui6Hy','Étudiante','2022-06-14 12:34:34',NULL),(11,'Afraitane','Heikal','heikalafraitane@gmail.com','$2y$10$.brBPjl2D4zC2H0EFI8RaOdtcyV4t.zVMwe6vmC8gOqHPBkPuARgO','Comité scientifique central','2022-06-20 19:25:24','934624a98e3581ca52ce5a06543021732565b128.png'),(12,'Kazouine','Naïfa','kazouinen@gmail.com','$2y$10$DtpTnaADbdGGYPKLyKTT8egBufZuD.EfSAk5FGPoV5y0f2u9bpFHO','Motivation','2022-06-21 12:46:14',NULL),(13,'Aboubacar','Nassim','nassimaboubacar@gmail.com','$2y$10$DWrpPT51crnHC1SC9Ryhr.whQSZS6ID0Oht49FX7KhhbSlBR9hN7K','Nassim_ab269','2022-11-08 17:19:07',NULL);
